import React, { useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";

function App() {
  // the useState hook for updating the list of notes on UI
  // useState() takes in initial state as parameter
  // returns an array containing the state and a setState function
  const [list, setList] = useState([]);

  // function addToList that adds a created note to our list of notes
  // takes in a note from the createArea component
  const addToList = (note) => {
    setList((prevValue) => {
      return [...prevValue, note];
    });
  };

  // functiion to create a single Note component
  const createNote = (noteItem) => {
    return <Note title={noteItem.title} content={noteItem.content} />;
  };

  return (
    <div>
      <Header />
      <CreateArea onAdd={addToList} />
      {list.map(createNote)}
      <Note key={1} title="Note title" content="Note content" />
      <Footer />
    </div>
  );
}

export default App;
